import './App.css';
import reactDom from 'react-dom';

function App() {
  // const style = {
  //   color: "red",
  //   backgroundColor: 'cyan'
  // }
  // const person1={
  //   name: "Ovijit karmoker",
  //   job: "Student"
  // }
  // const person2 = {
  //   name: "Sharmin Shama",
  //   job: "Data Entry"
  // }
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit done <code>src/App.js</code> and save to reload.
        </p>
        <Person name = "Ovijit" job = "Student"></Person>
        <Person name = "Shama" job = "Data Entry"></Person>
        {/* <h2 style = {style}>Name: {person1.name+" "+person1.job}</h2>
        <h2 style={{color: "pink", backgroundColor: "red"}}>Name: {person2.name+" "+person2.job}</h2> */}
        <p>My first react app</p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}
const name = ['Himel', 'Tinni', 'Rezaul']
function Person(props){
  const style = {
    color: 'black',
    backgroundColor: 'red',
    border: "5px solid orange",
    borderRadius: "10px",
    padding: "30px",
    margin: '10px'
  }
  return(
    <div style = {style}>
      <h1>Name: {props.name}</h1>
      <h4>Job: {props.job}</h4>
    </div>)
}

export default App;
